import React, { useRef } from 'react';
import emailjs from 'emailjs-com';
import styled from 'styled-components';

const Form = styled.form`
  height: 600px;
  width: 400px;
  box-shadow: 0 10px 10px rgba(0, 0, 0, 0.3), 0 0 50px rgba(0, 0, 0, 0.3);
  position: relative;
`;
const InputData = styled.div`
  height: 2rem;
  position: relative;
  display: flex;
  justify-content: center;
  margin-top: 50px;
`;
const ContactInput = styled.input`
  position: absolute;
  outline: none;
  border: none;
  background: none;
  border-bottom: 2px solid silver;
  width: 90%;
  bottom: 0.6rem;

  :focus ~ label {
    transform: translateY(-25px);
    font-size: 11px;
  }
`;
const ContactLabel = styled.label`
  position: absolute;
  left: 25px;
  bottom: 10px;
  width: 100%;
  pointer-events: none;
  transition: all 0.3s ease;
`;
export const ContactUs = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();
    emailjs
      .sendForm(
        'service_6bc61g1',
        'template_d0d97q9',
        e.target,
        'user_NdKIjOTJG11UTuJIbID06'
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
    e.target.reset();
  };

  return (
    <Form ref={form} onSubmit={sendEmail}>
      <InputData>
        <ContactInput type='text' name='subject' />
        <ContactLabel for='subject'>Temat</ContactLabel>
      </InputData>
      <InputData>
        <ContactInput type='text' name='name' />
        <ContactLabel for='subject'>Imie</ContactLabel>
      </InputData>
      <InputData>
        <ContactInput type='text' name='email' />
        <ContactLabel for='subject'>Email</ContactLabel>
      </InputData>
    </Form>
  );
};

